mapper.update:
UPDATE tb_product 
SET product_category_id = NULL
 WHERE product_category_id = #{productCategoryId};

mapper.select:
SELECT tb_product.product_id, tb_product.product_name, tb_product.product_desc, tb_product.img_addr, tb_product.normal_price, tb_product.promotion_price, tb_product.priority, tb_product.product_category_id, tb_product.enable_status, tb_product.shop_id, tb_product.create_time, tb_product.last_edit_time FROM tb_product
AND tb_product.shop_id = #{productCondition.shop.shopId}
ORDER BY tb_product.priority DESC LIMIT #{rowIndex},#{pageSize}
