mapper.update:
update tb_shop
shop_name=#{shopName}, shop_desc=#{shopDesc}, shop_addr=#{shopAddr}, phone=#{phone}, shop_img=#{shopImg}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, advice=#{advice}, area_id=#{area.areaId}, shop_category_id=#{shopCategory.shopCategoryId}
where shop_id=#{shopId}

mapper.select:
SELECT tb_shop.shop_id, tb_shop.shop_name, tb_shop.shop_desc, tb_shop.shop_addr, tb_shop.phone, tb_shop.shop_img, tb_shop.priority, tb_shop.create_time, tb_shop.last_edit_time, tb_shop.enable_status, tb_shop.advice, tb_area.area_id, tb_area.area_name, tb_shop_category.shop_category_id, tb_shop_category.shop_category_name, tb_person_info.user_id, tb_person_info.`name` FROM tb_shop INNER JOIN tb_area ON tb_shop.area_id = tb_area.area_id INNER JOIN tb_shop_category ON tb_shop.shop_category_id = tb_shop_category.shop_category_id INNER JOIN tb_person_info ON tb_shop.owner_id = tb_person_info.user_id
AND tb_shop_category.shop_category_id = #{shopCondition.shopCategory.shopCategoryId}
AND tb_shop_category.parent_id = #{shopCondition.shopCategory.parent.shopCategoryId}
ORDER BY tb_shop.priority LIMIT #{pageIndex},#{pageSize}
